import React from 'react';
import './App.css';
import Header from './components/Header';
export default function App() {
  const [prikazi, setPrikazi] = React.useState(false);

  React.useEffect(()=>{
    function promeni(){
      if(window.innerWidth > 770) setPrikazi(true);
      else setPrikazi(false);
    }
    window.addEventListener("resize", promeni);

    return function(){
      window.removeEventListener("resize", promeni);
    }

  },[])

  return (
    <div className='App.css'>
      <div className='kanta'>
        <Header/>
        {prikazi && <div>nesto</div>}
      </div>
    </div>
  );
}
