import React from "react"

export default function Nav(){
   const [show, setShow] = React.useState(false);

   const prikaziNavbar = () =>{
      if (window.screenY<300) setShow(true);
      else setShow(false);
   }

   React.useEffect(()=>{
      window.addEventListener('scroll',prikaziNavbar)
      return ()=> {
         window.removeEventListener('scroll',prikaziNavbar)
      }
   },[])

   return (
      <div className={`sajdNavigacija ${show && 'opac'}`}>
         
         <ul className="nav__links nesto">
               <li className="razmak"><a href="pocetak" ><span>//</span>  O nama</a></li>
               <li className="razmak"><a href="pocetak" ><span>//</span>  Proizvodi</a></li>
               <li className="razmak"><a href="pocetak"><span>//</span>  O kamenu</a></li>
               <li className="razmak"><a href="pocetak"><span>//</span>  Kontakt</a></li>
         </ul>
         
         
      </div>
   )
}
